import React from 'react';
import CssBaseline from '@material-ui/core/CssBaseline';
import HomePage from './HomePage';
import NoMatch from './utils/NoMatch';
import StepSelectionPage from './StepSelectionPage';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Longlist from './LonglistStep/Longlist';
import Definition from './DefinitionStep/Definition';
import ShortList from './ShortlistStep/ShortList';
import VisualizationPanel from './VisualizationStep/VisualizationPanel';
import Snackbar from './utils/Snackbar';
import Header from './utils/Header';

/**
 * TODO
 */
class App extends React.Component {
  /**
   * @ignore
   */
  constructor(props) {
    super(props);
    /**
     * TODO
     */
    this.state = {
      snackbars: []
    };

    this.handleSnackbarOpening = this.handleSnackbarOpening.bind(this);
    this.handleSnackbarClosing = this.handleSnackbarClosing.bind(this);
    this.generateSnackbars = this.generateSnackbars.bind(this);
  }
  /**
   * TODO
   */
  handleSnackbarOpening(msg, error) {
    let snackbars = this.state.snackbars;
    snackbars.push({ msg: msg, error: error });
    this.setState({ snackbars: snackbars });
  }
  /**
   * TODO
   */
  handleSnackbarClosing(i) {
    let snackbars = this.state.snackbars;
    snackbars.splice(i);
    this.setState({ snackbars: snackbars });
  }
  /**
   * TODO
   */
  generateSnackbars() {
    let snackbars = [];
    // eslint-disable-next-line
    for (let [i, s] of this.state.snackbars.entries()) {
      snackbars.push(
        <Snackbar
          key={i}
          snack={s}
          handleClose={() => this.handleSnackbarClosing(i)}
        />
      );
    }
    return snackbars;
  }
  /**
   * @ignore
   */ 
  render() {
    return (
      <div className='App'>
        <CssBaseline />
        <Router>
          <Header />
          <Switch>
            <Route exact path='/'>
              <HomePage />
            </Route>
            <Route exact path='/longlist'>
              <Longlist openSnackbar={this.handleSnackbarOpening} />
            </Route>
            <Route exact path='/shortlist'>
              <ShortList openSnackbar={this.handleSnackbarOpening} />
            </Route>
            <Route exact path='/definition'>
              <Definition openSnackbar={this.handleSnackbarOpening} />
            </Route>
            <Route exact path="/visualization">
              <VisualizationPanel/>
            </Route>
            <Route exact path='/steps'>
              <StepSelectionPage />
            </Route>
            <Route path="*">
              <NoMatch/>
            </Route>
          </Switch>
        </Router>
        {this.generateSnackbars()}
      </div>
    );
  }
}

export default App;

import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import DefinitionBoard from './DefinitionBoard';
import Fab from '@material-ui/core/Fab';
import { Link as RouterLink } from 'react-router-dom';

/**
 * @ignore
 */
const useStyles = makeStyles(theme => ({
  board : {
    maxHeight: "650px",
  },
  saveButton: {
    margin: 0,
    top: 7,
    right: 20,
    bottom: 'auto',
    left: 'auto',
    position: 'fixed',
  },
}));

/**
 * TODO
 */
export default function Definition(props) {
  const classes = useStyles();
  return (
    <div>
        <DefinitionBoard openSnackbar={props.openSnackbar} />
        <Fab color="secondary" component={RouterLink} to='/visualization' className={classes.saveButton} variant="extended">
          Open Visualization
        </Fab>
    </div>
  );
}

import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import DefinitionList from './DefinitionList';
import DefinitionCards from './DefinitionCards';
import update from 'immutability-helper';
import Api from '../../utils/Api';
import utils from '../../utils/utils'
import Fab from '@material-ui/core/Fab';

/**
 * @ignore
 */
const styles = theme => ({
  form: {
    paddingTop: theme.spacing(1)
  },
  list: {
    float: 'left',
    width: '25%',
    height: "650px",
    overflow: 'auto',
    marginRight: theme.spacing(2),
  },
  gridContainer: {
    float: 'left',
    width: '70%',
    height: "650px"
  },
  button: {
    left: '50%',
    margin: '1%'
  },
  saveButton: {
    margin: 0,
    top: 'auto',
    right: 20,
    bottom: 20,
    left: 'auto',
    position: 'fixed',
  }
});

/**
 * TODO
 */
class DefinitionBoard extends React.Component {
  /**
   * @ignore
   */
  constructor(props) {
    super(props);
    /**
     * TODO
     */
    this.state = {
      kpiList: [],
      selectedKpi: null
    };

    this.updateSelected = this.updateSelected.bind(this);
    /**
     * @ignore
     */
    this.handleChange = this.handleChange.bind(this);
    this.saveHandler = this.saveHandler.bind(this);
  }
  /**
   * @ignore
   */
  componentDidMount() {
    let kpisToInsert = [];
    let companyId=1;
    let userId=1;
    Api.get(`/kpi/${companyId}/${userId}/`)
    .then(data => {
      // eslint-disable-next-line
      let shortlistedKpis = data.filter(k => k.status>=2)
      shortlistedKpis.sort((a, b) => (a.id > b.id) ? 1 : -1)
      for(let k of shortlistedKpis){
        kpisToInsert.push({
          id:k.id,
          name:k.name,
          defined:this.getDefinitionState(k),
          definitionField: new Map([
            ['purpose', k.purpose],
            ['customers', k.customers],
            ['datasources', k.datasources],
            ['formula', k.formula],
            ['resources', k.resources],
            ['problems', k.problems],
            ['targets', k.targets],
            ['outcomes', k.outcomes],
            ['cost', k.cost]
          ])
        })
      }
      this.setState({ kpiList: kpisToInsert });
    })
    .catch(error => {
      console.error(error);
      this.props.openSnackbar('An error ocurred while loading the KPIs', true);
    });
  }
  /**
   * TODO
   */
  getDefinitionState(data) {
    let fields = [data.purpose,
      data.customers,
      data.datasources,
      data.formula,
      data.resources,
      data.problems,
      data.targets,
      data.outcomes,
      data.cost
    ]
    
    if(fields.every((e)=>e!==null)){
      return utils.KpiDefinitionState.DEFINED
    }
    else if(fields.every((e)=>e===null)){
      return utils.KpiDefinitionState.UNDEFINED
    }
    else {
      return utils.KpiDefinitionState.PARTIALLY_DEFINED
    }
  }
  /**
   * TODO
   */
  updateSelected(selectedIndex) {
    this.setState({
      selectedKpi: update(this.state.selectedKpi, { $set: selectedIndex })
    });
  }
  /**
   * TODO
   */
  handleChange = (e, i, n) => {
    let newValue = e.target.value===''?null:e.target.value
    this.setState({
      kpiList: update(this.state.kpiList, {
        [i]: { definitionField: { [n]: { $set: newValue } } }
      })
    });
  };
  /**
   * TODO
   */
  saveHandler() {
    let kpiToSave = this.state.kpiList[this.state.selectedKpi];

    let jsonPayload = JSON.stringify({
      companyId: "1",
      userId: "1",
      kpiId: kpiToSave.id,
      definition: {
        purpose: kpiToSave.definitionField.get('purpose'),
        customers: kpiToSave.definitionField.get('customers'),
        datasources: kpiToSave.definitionField.get('datasources'),
        formula: kpiToSave.definitionField.get('formula'),
        resources: kpiToSave.definitionField.get('resources'),
        problems: kpiToSave.definitionField.get('problems'),
        targets: kpiToSave.definitionField.get('targets'),
        outcomes: kpiToSave.definitionField.get('outcomes'),
        cost: kpiToSave.definitionField.get('cost')
      }
    })

    Api.post('/kpi/definition',jsonPayload)
    .then(data=>{
      let updatedKpi = {
        id:data.id,
        name:data.name,
        defined:this.getDefinitionState(data),
        definitionField: new Map([
          ['purpose', data.purpose],
          ['customers', data.customers],
          ['datasources', data.datasources],
          ['formula', data.formula],
          ['resources', data.resources],
          ['problems', data.problems],
          ['targets', data.targets],
          ['outcomes', data.outcomes],
          ['cost', data.cost]
        ])
      }
      this.setState({
        kpiList: update(this.state.kpiList, {
          [this.state.selectedKpi]:  { $set: updatedKpi }
        })
      })
      this.props.openSnackbar('KPI definition saved');
    })
    .catch(err=>{
      console.error(err)
      this.props.openSnackbar('An error ocurred while saving this KPI definition', true);
    })

    
  }
  /**
   * @ignore
   */ 
  render() {
    const { classes } = this.props;
    return (
      <div>
        <div className={classes.list}>
          <DefinitionList
            kpiList={this.state.kpiList.map(k => ({name:k.name,defined:k.defined}))}
            selectedKpi={this.state.selectedKpi}
            updateSelected={this.updateSelected}
          />
        </div>
        <div className={classes.form}>
          {this.state.selectedKpi !== null ? (
            <div className={classes.gridContainer}>
              <DefinitionCards
                kpiList={this.state.kpiList[this.state.selectedKpi]}
                handleChange={(e, name) => {
                  this.handleChange(e, this.state.selectedKpi, name);
                }}
              />
            </div>
          ) : (
            <Typography variant='h2' gutterBottom align='center'>
              Please choose a KPI
            </Typography>
          )}
        </div>
        {this.state.selectedKpi!== null && (
        <Fab color="primary" className={classes.saveButton} onClick={this.saveHandler} variant="extended">
          SAVE
        </Fab>)}
      </div>
    );
  }
}

export default withStyles(styles)(DefinitionBoard);

import React from 'react';
import { withStyles } from '@material-ui/core/styles';

import {
  Card,
  Typography,
  CardContent,
  TextField,
  Grid,
  Icon
} from '@material-ui/core';

/**
 * @ignore
 */
const styles = theme => ({
  img: {
    width: 30,
    float: 'right'
  },
  field: {
    marginTop: 0
  }
});

/**
 * TODO
 */
class DefinitionCards extends React.Component {
  /**
   * TODO
   */
  handleChange(e, name) {
    this.props.handleChange(e, name);
  }
  /**
   * TODO
   */
  createCard(key, name, text) {
    const { classes } = this.props;
    return (
      <Card>
        <CardContent>
          <Icon>
            <img
              className={classes.img}
              src={`/content/svg_icon/${key}.svg`}
              alt={name}
            />
          </Icon>
          <Typography variant='h6'>{name}</Typography>
          <Typography variant='subtitle2' color='textSecondary'>
            {text}
          </Typography>
          <TextField
            className={classes.field}
            onChange={e => this.handleChange(e, key)}
            id='standard-basic'
            label='Type here'
            margin='normal'
            value={this.props.kpiList.definitionField.get(key)||''}
          />
        </CardContent>
      </Card>
    );
  }
  /**
   * TODO
   */
  generateCards() {
    const { classes } = this.props;
    return (
      <Grid container spacing={1}>
        <Grid item xs={12}>
          <Card>
            <CardContent>
              <Icon>
                <img
                  className={classes.img}
                  src={`/content/svg_icon/name.svg`}
                  alt='KPI Name'
                />
              </Icon>
              <Typography variant='h6'>KPI Name</Typography>
              <Typography variant='subtitle2'>
                {this.props.kpiList.name}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={4}>
          {this.createCard('purpose', 'Purpose', 'Why should we measure this?')}
        </Grid>
        <Grid item xs={4}>
          {this.createCard(
            'formula',
            'Definition or Formula',
            "If there's any calculation, how the measure is worked out?"
          )}
        </Grid>
        <Grid item xs={4}>
          {this.createCard(
            'targets',
            'Targets',
            'What score do we want to achieve? (if already know)'
          )}
        </Grid>
        <Grid item xs={4}>
          {this.createCard('customers', 'Customer', 'Who will use this KPI?')}
        </Grid>
        <Grid item xs={4}>
          {this.createCard(
            'resources',
            'Production Resources',
            'What resources are needed to produce the KPI and reports?'
          )}
        </Grid>
        <Grid item xs={4}>
          {this.createCard(
            'outcomes',
            'Targets Outcomes',
            'What score do we want to achieve? (if already know)'
          )}
        </Grid>
        <Grid item xs={4}>
          {this.createCard(
            'datasources',
            'Data Sources',
            'Where will the KPI data come from?'
          )}
        </Grid>
        <Grid item xs={4}>
          {this.createCard(
            'problems',
            'Problems and Errors',
            'What are the known issues with KPI production & accuracy?'
          )}
        </Grid>
        <Grid item xs={4}>
          {this.createCard(
            'cost',
            'Production Cost',
            'What is the cost of implementing and producing the KPI?'
          )}
        </Grid>
      </Grid>
    );
  }
  /**
   * @ignore
   */
  render() {
    return <div>{this.generateCards()}</div>;
  }
}

export default withStyles(styles)(DefinitionCards);

import React from 'react';
import { List, ListItem, ListItemText, Typography } from '@material-ui/core';
import utils from '../../utils/utils'

/**
 * TODO
 */
class DefinitionList extends React.Component {
  /**
   * TODO
   */
  generateKpiList() {
    let kpiList = [];
    // eslint-disable-next-line
    for (let [i, k] of this.props.kpiList.entries()) {
      kpiList.push(
        <ListItem
          key={i}
          button
          selected={this.props.selectedKpi === i}
          onClick={() => this.props.updateSelected(i)}
        >
          <ListItemText primary={k.name} />
          {
            this.props.kpiList[i].defined === utils.KpiDefinitionState.DEFINED ? (
              <Typography align='right' color='textSecondary'>
                Defined
              </Typography>
            ) : this.props.kpiList[i].defined === utils.KpiDefinitionState.PARTIALLY_DEFINED ? (
              <Typography align='right' color='textSecondary'>
                Partially defined
              </Typography>
            ) : (
              <Typography align='right' color='textSecondary'>
                Undefined
              </Typography>
            )
          }
        </ListItem>
      );
    }
    return kpiList;
  }
  /**
   * @ignore
   */
  render() {
    return <List>{this.generateKpiList()}</List>;
  }
}

export default DefinitionList;

import React from 'react';
import Link from '@material-ui/core/Link';
import Paper from '@material-ui/core/Paper';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { withStyles } from '@material-ui/core/styles';
import SignUp from './SignUp';
import SignIn from './SignIn';

/**
 * @ignore
 */
const styles = theme => ({
  root: {
    height: '100vh',
  },
  image: {
    backgroundImage: 'url(/content/images/homepage.png)',
    backgroundRepeat: 'no-repeat',
    backgroundColor: theme.palette.grey[50],
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  }
});

/**
 * TODO
 */
class HomePage extends React.Component{
  /**
   * @ignore
   */
  constructor(props){
    super(props)

    this.switchForm = this.switchForm.bind(this)
    this.generateSignUp = this.generateSignUp.bind(this)
    this.generateSignIn = this.generateSignIn.bind(this)
    
    /**
     * TODO
     */
    this.state = {
      form : this.generateSignUp
    }
  }
  /**
   * TODO
   */
  switchForm() {
    this.setState({form:this.state.form===this.generateSignUp?this.generateSignIn:this.generateSignUp})
  }
  /**
   * TODO
   */
  generateSignUp() {
    return <SignUp switchForm={this.switchForm}/>
  }
  /**
   * TODO
   */
  generateSignIn() {
    return <SignIn switchForm={this.switchForm}/>
  }
  /**
   * TODO
   */
  copyright() {
    return (
      <Typography variant="body2" color="textSecondary" align="center">
        {'Copyright  '}
        <Link color="inherit" href="https://jugo.nl/en/">
          JUGO
        </Link>{' '}
        {new Date().getFullYear()}
        {'.'}
      </Typography>
    );
  }
  /**
   * @ignore
   */
  render(){
    const { classes } = this.props;

    return (
      <Grid container component="main" className={classes.root}>
        <Grid item xs={false} sm={4} md={7} className={classes.image} />
        <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
          {this.state.form()}  
          <Box mt={5}>
            {this.copyright()}
          </Box>
        </Grid>
      </Grid>
    );
  }
  
}

export default withStyles(styles, { withTheme: true })(HomePage);

import React from 'react';
import Container from '@material-ui/core/Container';
import LonglistBoard from './LonglistBoard';
import { makeStyles } from '@material-ui/core/styles';

/**
 * @ignore
 */
const useStyles = makeStyles(theme => ({
  root: {
    paddingTop : theme.spacing(10)
  },
  card: {
    height: '100%',
    display: 'flex',
    flexDirection: 'column'
  },
  cardMedia: {
    paddingTop: '56.25%', // 16:9
  },
  cardContent: {
    flexGrow: 1
  }
}));

/**
 * TODO
 */
export default function Longlist(props) {
  const classes = useStyles();

  return (
    <Container>
      <div  className={classes.root}>
        <LonglistBoard openSnackbar={props.openSnackbar}/>
      </div>
    </Container>
  );
}

import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import {
  Grid,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Checkbox,
  Typography,
  ListSubheader,
  FormControlLabel,
  TextField
} from '@material-ui/core';
import { Button } from '@material-ui/core';
import update from 'immutability-helper';
import LonglistXMLImport from './LonglistXMLImport';
import LonglistManualImport from './LonglistManualImport';
import Api from '../../utils/Api';

/**
 * @ignore
 */
const styles = theme => ({
  listRoot: {
    textAlign: 'center'
  },
  list: {
    overflow: 'auto',
    maxHeight: 400,
    marginBottom: theme.spacing(2)
  }
});

/**
 * TODO
 */
class LonglistBoard extends React.Component {
  /**
   * @ignore
   */
  constructor(props) {
    super(props);
    /**
     * TODO
     */
    this.state = {
      kpiList: [],
      batchCheck: true,
    };
    this.handleChecked = this.handleChecked.bind(this);
    this.handleBatchCheck = this.handleBatchCheck.bind(this);
    this.handleKpiToggleEdition = this.handleKpiToggleEdition.bind(this);
    this.handleValidateKpiEdition = this.handleValidateKpiEdition.bind(this);
    this.saveHandler = this.saveHandler.bind(this);
    this.importKpis = this.importKpis.bind(this);
    this.addKpis = this.addKpis.bind(this);
  }
  /**
   * TODO
   */
  handleChecked(id) {
    let currentState = this.state.kpiList[id].isChecked;
    this.setState({
      kpiList: update(this.state.kpiList, {
        [id]: { isChecked: { $set: !currentState } }
      })
    });
  }
  /**
   * TODO
   */
  handleBatchCheck() {
    let newState = !this.state.batchCheck
    this.setState({batchCheck:newState})
    let kpiList = this.state.kpiList
    for(let k of kpiList){
      k.isChecked = newState
    }
    this.setState({kpiList:kpiList})
  }
  /**
   * TODO
   */
  handleKpiToggleEdition(id){
    this.setState({
      kpiList: update(this.state.kpiList, {
        [id]: { isBeingEdited: { $set: true } }
      })
    });
  }
  /**
   * TODO
   */
  handleKpiEdition(event,id){
    this.setState({
      kpiList: update(this.state.kpiList, {
        [id]: { name : { $set: event.target.value } }
      })
    });
  }
  /**
   * TODO
   */
  handleEnterValidateKpiEdition(event,id) {
    if(event.key==='Enter'){
      this.handleValidateKpiEdition(id)
    }
  }
  /**
   * TODO
   */
  handleValidateKpiEdition(id){
    this.setState({
      kpiList: update(this.state.kpiList, {
        [id]: { isBeingEdited: { $set: false } }
      })
    });
  }
  /**
   * TODO
   */
  generateKpiList() {
    let kpiList = [];
    // eslint-disable-next-line
    for (let [i, k] of this.state.kpiList.entries()) {
      kpiList.push(
        <ListItem key={i}>
          {k.isBeingEdited?<TextField autoFocus value={this.state.kpiList[i].name} onKeyDown={(e) => this.handleEnterValidateKpiEdition(e,i)} onChange={(e) => this.handleKpiEdition(e,i)} onBlur={() => this.handleValidateKpiEdition(i)}/>:<ListItemText primary={k.name} onDoubleClick={() => this.handleKpiToggleEdition(i)}/>}
          <ListItemIcon>
            <Checkbox
              disableRipple
              onChange={() => this.handleChecked(i)}
              checked={k.isChecked}
            />
          </ListItemIcon>
        </ListItem>
      );
    }
    return kpiList;
  }
  /**
   * TODO
   */
  saveHandler() {
    let kpisToSave = this.state.kpiList
      .filter(e => e.isChecked)
      .map(e => ({ name: e.name }));
    let jsonPayload = JSON.stringify({
      companyId: 1,
      userId: 1,
      kpis: kpisToSave
    });

    Api.post('/kpi', jsonPayload)
      .then(data => {
        this.props.openSnackbar(
          `${data.length} KPI(s) have been saved`,
          false
        );
        this.setState({ kpiList: [] });
      })
      .catch(error => {
        console.error(error);
        this.props.openSnackbar('An error ocurred while saving the KPIs', true);
      });
  }
  /**
   * TODO
   */
  importKpis(list) {
    this.setState({
      kpiList: update(this.state.kpiList, {
        $push: list.map(e => ({ name: e, isChecked: this.state.batchCheck, isBeingEdited : false }))
      })
    });
  }
  /**
   * TODO
   */
  addKpis(name) {
    let newKpi = { name: name, isChecked: this.state.batchCheck, isBeingEdited : false };
    this.setState({ kpiList: update(this.state.kpiList, { $push: [newKpi] }) });
  }
  /**
   * @ignore
   */
  render() {
    const { classes } = this.props;

    return (
      <div className={classes.listRoot}>
        <Typography variant='h2' gutterBottom>
          {this.state.kpiList.length !== 0
            ? this.state.kpiList.length + ' KPIs found'
            : 'Please import some KPIs'}
        </Typography>
        <Grid container spacing={10}>
          <Grid item xs={4}>
            <Typography variant='h5' gutterBottom>
              {'Add KPI manually'}
            </Typography>
            <LonglistManualImport addKpis={this.addKpis} />
          </Grid>
          <Grid item xs={4}>
            <List className={classes.list}>
              <ListSubheader
              disableSticky
              >
                <FormControlLabel
                  control={<Checkbox color="primary" checked={this.state.batchCheck} onClick={this.handleBatchCheck}/>}
                  label={'Check All'}
                  labelPlacement="start"
                />
              </ListSubheader>
              {this.generateKpiList()}
            </List>
            <Button
              variant='contained'
              disabled={this.state.kpiList.length === 0}
              color='primary'
              onClick={this.saveHandler}
            >
              save
            </Button>
          </Grid>
          <Grid item xs={4}>
            <Typography variant='h5' gutterBottom>
              {'Import a KPI file'}
            </Typography>
            <LonglistXMLImport
              importKpis={this.importKpis}
              openSnackbar={this.props.openSnackbar}
            />
          </Grid>
        </Grid>
      </div>
    );
  }
}

