class VisualizationPanel extends Component {
    /**
     * @ignore
     */  
    constructor(props) {
        super(props);
        /**
         * TODO
         */
        this.state = {
            definedKPIData: [],
            numberKPIData:[],
            totalScoreLabel:[],
            totalScoreData: []
        }
    }
    
    /**
     * @ignore
