            //eslint-disable-next-line
            for (let k in data) {
                totalScoreLabel.push(utils.trimStringToFit(data[k].name));
                totalScoreData.push(data[k].score);               
            }
            this.setState({ 
                totalScoreLabel:totalScoreLabel,
                totalScoreData: totalScoreData        
            })  
        });
