import React from 'react';
import { withStyles } from '@material-ui/core/styles';

import {
  Card,
  Typography,
  CardContent,
  TextField,
  Grid,
  Icon
} from '@material-ui/core';

/**
 * @ignore
 */
const styles = theme => ({
  img: {
    width: 30,
    float: 'right'
  },
  field: {
    marginTop: 0
  }
});

class DefinitionCards extends React.Component {
  handleChange(e, name) {
    this.props.handleChange(e, name);
  }

  createCard(key, name, text) {
    const { classes } = this.props;
    return (
      <Card>
        <CardContent>
          <Icon>
            <img
              className={classes.img}
              src={`/content/svg_icon/${key}.svg`}
              alt={name}
            />
          </Icon>
          <Typography variant='h6'>{name}</Typography>
          <Typography variant='subtitle2' color='textSecondary'>
            {text}
          </Typography>
          <TextField
            className={classes.field}
            onChange={e => this.handleChange(e, key)}
            id='standard-basic'
            label='Type here'
            margin='normal'
            value={this.props.kpiList.definitionField.get(key)||''}
          />
        </CardContent>
      </Card>
    );
  }

  generateCards() {
    const { classes } = this.props;
    return (
      <Grid container spacing={1}>
        <Grid item xs={12}>
          <Card>
            <CardContent>
              <Icon>
                <img
                  className={classes.img}
                  src={`/content/svg_icon/name.svg`}
                  alt='KPI Name'
                />
              </Icon>
              <Typography variant='h6'>KPI Name</Typography>
              <Typography variant='subtitle2'>
                {this.props.kpiList.name}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={4}>
          {this.createCard('purpose', 'Purpose', 'Why should we measure this?')}
        </Grid>
        <Grid item xs={4}>
          {this.createCard(
            'formula',
            'Definition or Formula',
            "If there's any calculation, how the measure is worked out?"
          )}
        </Grid>
        <Grid item xs={4}>
          {this.createCard(
            'targets',
            'Targets',
            'What score do we want to achieve? (if already know)'
          )}
        </Grid>
        <Grid item xs={4}>
          {this.createCard('customers', 'Customer', 'Who will use this KPI?')}
        </Grid>
        <Grid item xs={4}>
          {this.createCard(
            'resources',
            'Production Resources',
            'What resources are needed to produce the KPI and reports?'
          )}
        </Grid>
        <Grid item xs={4}>
          {this.createCard(
            'outcomes',
            'Targets Outcomes',
            'What score do we want to achieve? (if already know)'
          )}
        </Grid>
        <Grid item xs={4}>
          {this.createCard(
            'datasources',
            'Data Sources',
            'Where will the KPI data come from?'
          )}
        </Grid>
        <Grid item xs={4}>
          {this.createCard(
            'problems',
            'Problems and Errors',
            'What are the known issues with KPI production & accuracy?'
          )}
        </Grid>
        <Grid item xs={4}>
          {this.createCard(
            'cost',
            'Production Cost',
            'What is the cost of implementing and producing the KPI?'
          )}
        </Grid>
      </Grid>
    );
  }

  render() {
    return <div>{this.generateCards()}</div>;
  }
}

export default withStyles(styles)(DefinitionCards);

import React from 'react';
import { List, ListItem, ListItemText, Typography } from '@material-ui/core';
import KpiDefinitionState from '../../utils/utils'

class DefinitionList extends React.Component {
  generateKpiList() {
    let kpiList = [];
    // eslint-disable-next-line
    for (let [i, k] of this.props.kpiList.entries()) {
      kpiList.push(
        <ListItem
          key={i}
          button
          selected={this.props.selectedKpi === i}
          onClick={() => this.props.updateSelected(i)}
        >
          <ListItemText primary={k.name} />
          {
            this.props.kpiList[i].defined === KpiDefinitionState.DEFINED ? (
              <Typography align='right' color='textSecondary'>
                Defined
              </Typography>
            ) : this.props.kpiList[i].defined === KpiDefinitionState.PARTIALLY_DEFINED ? (
              <Typography align='right' color='textSecondary'>
                Partially defined
              </Typography>
            ) : (
              <Typography align='right' color='textSecondary'>
                Undefined
              </Typography>
            )
          }
        </ListItem>
      );
    }
    return kpiList;
  }

  render() {
    return <List>{this.generateKpiList()}</List>;
  }
}

