import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import {
  Grid,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Checkbox,
  Typography
} from '@material-ui/core';
import { Button } from '@material-ui/core';
import update from 'immutability-helper';
import LonglistXMLImport from './LonglistXMLImport';
import LonglistManualImport from './LonglistManualImport';
import Api from '../../utils/Api';

/**
 * @ignore
 */
const styles = theme => ({
  listRoot: {
    textAlign: 'center'
  },
  list: {
    overflow: 'auto',
    maxHeight: 400,
    marginBottom: theme.spacing(2)
  }
});

class LonglistBoard extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      kpiList: []
    };
    this.handleChecked = this.handleChecked.bind(this);
    this.saveHandler = this.saveHandler.bind(this);
    this.importKpis = this.importKpis.bind(this);
    this.addKpis = this.addKpis.bind(this);
  }

  handleChecked(id) {
    let currentState = this.state.kpiList[id].isChecked;
    this.setState({
      kpiList: update(this.state.kpiList, {
        [id]: { isChecked: { $set: !currentState } }
      })
    });
  }

  generateKpiList() {
    let kpiList = [];
    // eslint-disable-next-line
    for (let [i, k] of this.state.kpiList.entries()) {
      kpiList.push(
        <ListItem key={i}>
          <ListItemText primary={k.name} />
          <ListItemIcon>
            <Checkbox
              disableRipple
              onChange={() => this.handleChecked(i)}
              checked={k.isChecked}
            />
          </ListItemIcon>
        </ListItem>
      );
    }
    return kpiList;
  }

  saveHandler() {
    let kpisToSave = this.state.kpiList
      .filter(e => e.isChecked)
      .map(e => ({ name: e.name }));
    let jsonPayload = JSON.stringify({
      companyId: 1,
      userId: 1,
      kpis: kpisToSave
    });

    Api.post('/kpi', jsonPayload)
      .then(data => {
        this.props.openSnackbar(
          `${data.length} KPI(s) have been saved`,
          false
        );
        this.setState({ kpiList: [] });
      })
      .catch(error => {
        console.error(error);
        this.props.openSnackbar('An error ocurred while saving the KPIs', true);
      });
  }

  importKpis(list) {
    this.setState({
      kpiList: update(this.state.kpiList, {
        $push: list.map(e => ({ name: e, isChecked: true }))
      })
    });
  }

  addKpis(name) {
    let newKpi = { name: name, isChecked: true };
    this.setState({ kpiList: update(this.state.kpiList, { $push: [newKpi] }) });
  }

  render() {
    const { classes } = this.props;

    return (
      <div className={classes.listRoot}>
        <Typography variant='h2' gutterBottom>
          {this.state.kpiList.length !== 0
            ? this.state.kpiList.length + ' KPIs found'
            : 'Please import some KPIs'}
        </Typography>
        <Grid container spacing={10}>
          <Grid item xs={4}>
            <Typography variant='h5' gutterBottom>
              {'Add KPI manually'}
            </Typography>
            <LonglistManualImport addKpis={this.addKpis} />
          </Grid>
          <Grid item xs={4}>
            <List className={classes.list}>{this.generateKpiList()}</List>
            <Button
              variant='contained'
              disabled={this.state.kpiList.length === 0}
              color='primary'
              onClick={this.saveHandler}
            >
              save
            </Button>
          </Grid>
          <Grid item xs={4}>
            <Typography variant='h5' gutterBottom>
              {'Import a KPI file'}
            </Typography>
            <LonglistXMLImport
              importKpis={this.importKpis}
              openSnackbar={this.props.openSnackbar}
            />
          </Grid>
        </Grid>
      </div>
    );
  }
}

export default withStyles(styles)(LonglistBoard);

import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import { TextField, Fab } from '@material-ui/core';
import AddIcon from '@material-ui/icons/Add';

/**
 * @ignore
 */
const styles = theme => ({
  element: {
    'vertical-align': 'bottom',
    'margin-left': 25
  }
});

class LonglistManualImport extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      name: ''
    };

    this.addField = this.addField.bind(this);
    this.handleKeyPress = this.handleKeyPress.bind(this);
  }

  handleChange = e =>
    this.setState({
      name: e.target.value
    });

  handleKeyPress = event => {
    if (event.key === 'Enter') {
      this.addField();
    }
  };

  addField(event) {
    this.props.addKpis(this.state.name);
    this.setState({ name: '' });
  }

  render() {
    const { classes } = this.props;

    return (
      <div>
        <div>
          <TextField
            id='standard-basic'
            className={classes.element}
            value={this.state.name}
            onChange={this.handleChange}
            onKeyPress={this.handleKeyPress}
            label="KPI's name"
            margin='normal'
          />
          <Fab
            size='small'
            color='primary'
            aria-label='add'
            disabled={this.state.name === ''}
            onClick={this.addField}
            className={classes.element}
          >
            <AddIcon />
          </Fab>
        </div>
      </div>
    );
  }
}

export default withStyles(styles)(LonglistManualImport);

import React from 'react';
import Parser from 'xml2js';
import Fab from '@material-ui/core/Fab';
import NoteAddIcon from '@material-ui/icons/NoteAdd';

class LonglistXMLImport extends React.Component {
  constructor(props) {
    super(props);

    this.handleChange = this.handleChange.bind(this);
    this.extractSchema = this.extractSchema.bind(this);
  }

  extractSchema(schema) {
    let kpiList = [];
    try {
      if (
        schema.Report[
          'VisRpt:VisioReport'
        ][0].ReportFields[0].ReportField.findIndex(
          e => e.Name[0] === 'Displayed Text'
        ) !== undefined
      ) {
        let previousIndexes = schema.Report[
          'VisRpt:VisioReport'
        ][0].Group[0].GroupField[0].RowItem.reduce((a, e, i) => {
          if (e.Field[0].Val) {
            if (e.Field[0].Val[0]._ === 'KPI') a.push(i);
          }
          return a;
        }, []);

        // eslint-disable-next-line
        for (let i of previousIndexes) {
          kpiList.push(
            schema.Report['VisRpt:VisioReport'][0].Group[0].GroupField[0]
              .RowItem[i + 1].Field[0].Val[0]._
          );
        }
        this.props.importKpis(kpiList);
      } else throw new Error('No text field in the report');
    } catch (error) {
      this.props.openSnackbar(
        'Error while extracting the schema from the XML file',
        true
      );
      console.error(
        `Error while extracting the schema from the XML file. Please ensure than the imported XML file is a Visio report: ${error}`
      );
    }
  }

  handleChange(event) {
    try {
      let reader = new FileReader();
      reader.readAsText(event.target.files[0], 'UTF-8');
      reader.onload = evt => {
        Parser.parseString(evt.target.result, (err, result) => {
          this.extractSchema(result);
        });
      };
      reader.onerror = evt => {
        throw new Error('FileReader error');
      };
    } catch (error) {
      this.props.openSnackbar('Error while reading the imported file', true);
      console.error(`Error while reading the imported file: ${error}`);
    }
